import React, { useState, useEffect } from "react";
import RadarComparisonChart from "../components/radarChart/RadarComparisonRatio"; // Radar chart component
import "./explore.css";

function MultiSpaceExplore() {
  const [allSubspaces, setAllSubspaces] = useState([]);
  const [selectedComparison, setSelectedComparison] = useState({
    subspace1: "",
    subspace2: "",
    metric: "avg", // Metric is fixed to "avg"
  });

  useEffect(() => {
    // Load subspaces from localStorage
    const savedSubspaces = JSON.parse(localStorage.getItem("subspaces")) || [];
    setAllSubspaces(savedSubspaces);

    // Automatically set the first two subspaces for comparison
    if (savedSubspaces.length >= 2) {
      setSelectedComparison({
        subspace1: savedSubspaces[0]?.id || "",
        subspace2: savedSubspaces[1]?.id || "",
        metric: "avg",
      });
    }
  }, []);

  const handleSubspaceChange = (event, subspaceKey) => {
    const newSubspace = event.target.value;
    setSelectedComparison((prevComparison) => ({
      ...prevComparison,
      [subspaceKey]: newSubspace,
    }));
  };

  return (
    <div className="explore-page">
      {/* Header Section */}
      <div className="header">
        <h1>Multi-Subspace Exploration</h1>
        <div className="subspace-name">
          <div>
            <label>Subspace 1:</label>
            <select
              className="subspace-dropdown"
              value={selectedComparison.subspace1}
              onChange={(e) => handleSubspaceChange(e, "subspace1")}
            >
              <option value="">Select Subspace 1</option>
              {allSubspaces.map((subspace) => (
                <option key={subspace.id} value={subspace.id}>
                  {subspace.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label>Subspace 2:</label>
            <select
              className="subspace-dropdown"
              value={selectedComparison.subspace2}
              onChange={(e) => handleSubspaceChange(e, "subspace2")}
            >
              <option value="">Select Subspace 2</option>
              {allSubspaces.map((subspace) => (
                <option key={subspace.id} value={subspace.id}>
                  {subspace.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Plots Section */}
      <div className="plots-container">
        {selectedComparison.subspace1 && selectedComparison.subspace2 ? (
          <div className="plot-section">
            <h2>
              Subspace {selectedComparison.subspace1} vs Subspace{" "}
              {selectedComparison.subspace2} (AVG)
            </h2>
            <RadarComparisonChart
              subInd1={selectedComparison.subspace1}
              subInd2={selectedComparison.subspace2}
              metric={selectedComparison.metric}
            />
          </div>
        ) : (
          <div className="plot-section">
            <h2>Please select two subspaces to compare.</h2>
          </div>
        )}
      </div>
    </div>
  );
}

export default MultiSpaceExplore;
