import React from "react";
import Plot from 'react-plotly.js';

const CorrelationHeatmap = ({ matrix, attributes }) => {
  return (
      <Plot
          data={[
              {
                  z: matrix,
                  x: attributes,
                  y: attributes,
                  type: "heatmap",
                  colorscale: "Viridis",
                  zmin: -1, // Force the color scale to match correlation range
                  zmax: 1,
              },
          ]}
          layout={{
              title: "Correlation Matrix",
              xaxis: { title: "Attributes", automargin: true },
              yaxis: { title: "Attributes", automargin: true },
              annotations: matrix.flatMap((row, i) =>
                  row.map((value, j) => ({
                      x: attributes[j],
                      y: attributes[i],
                      text: value.toFixed(2), // Display value with 2 decimals
                      font: {
                          size: 10,
                          color: "black",
                      },
                      showarrow: false,
                  }))
              ),
              width: 700,
              height: 700,
          }}
      />
  );
};

export default CorrelationHeatmap;
