import React, { useState, useEffect } from "react";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, Tooltip, Legend } from "chart.js";
import { Bubble } from "react-chartjs-2";
import mockCorrelationMatrix from "../../mockDataM.json";

// Register required Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, Tooltip, Legend);

const CorrelationMatrixHeatmap = () => {
  const [matrixData, setMatrixData] = useState(null);

  useEffect(() => {
    // Simulate API call by loading mock data
    setMatrixData(mockCorrelationMatrix.correlation_matrix);
  }, []);

  if (!matrixData) {
    return <p>Loading...</p>;
  }

  // Extract feature names (keys of the correlation matrix)
  const features = Object.keys(matrixData);

  // Generate bubble data
  const bubbleData = [];
  features.forEach((rowKey, rowIndex) => {
    features.forEach((colKey, colIndex) => {
      const value = matrixData[rowKey][colKey];
      bubbleData.push({
        x: colIndex,
        y: rowIndex,
        r: Math.abs(value) * 10, // Adjust bubble size based on correlation magnitude
        backgroundColor: value > 0 ? `rgba(0, 255, 0, ${value})` : `rgba(255, 0, 0, ${Math.abs(value)})`, // Green for positive, red for negative
      });
    });
  });

  // Generate heatmap chart data
  const chartData = {
    labels: features,
    datasets: [
      {
        label: "Correlation Matrix",
        data: bubbleData,
        backgroundColor: bubbleData.map((point) => point.backgroundColor),
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: true,
    scales: {
      x: {
        type: "category",
        labels: features,
        title: {
          display: true,
          text: "Features",
        },
      },
      y: {
        type: "category",
        labels: features,
        title: {
          display: true,
          text: "Features",
        },
      },
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: (context) => {
            const value = matrixData[features[context.raw.y]][features[context.raw.x]];
            return `Correlation: ${value.toFixed(2)}`;
          },
        },
      },
      legend: {
        display: false, // Optional: Hide legend
      },
    },
  };

  return (
    <div>
      <h1>Correlation Matrix Heatmap</h1>
      <Bubble data={chartData} options={options} />
    </div>
  );
};

export default CorrelationMatrixHeatmap;
