import React, { useEffect, useState } from "react";
import "./infoPanel.css";

function InfoPanel({ isOpen, onClose }) {
  const [descriptions, setDescriptions] = useState([]);

  useEffect(() => {
    if (isOpen) {
      fetch("../../attributeDescription.json")
        .then((res) => res.json())
        .then((data) => setDescriptions(data))
        .catch((err) => console.error("Error loading descriptions:", err));
    }
  }, [isOpen]);

  return (
    <>
      {isOpen && (
        <div className="info-panel-overlay" onClick={onClose}>
          <div className="info-panel" onClick={(e) => e.stopPropagation()}>
            <h2>Information</h2>
            <div className="variable-info">
              {descriptions.map((variable) => (
                <div key={variable["Variable Name"]} className="variable-section">
                  <h3>{variable["Full Name"]}</h3>
                  <p><strong>Description:</strong> {variable["Description"]}</p>
                  <p><strong>Details:</strong> {variable["Extended Description"]}</p>
                </div>
              ))}
            </div>
            <button onClick={onClose} className="close-btn">
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
}

export default InfoPanel;
