import React, { useState, useEffect } from "react";
import Plot from "react-plotly.js";
import axios from "axios";

const DimensionalityReductionPlot = ({ subspaceId, nComponents = 2 }) => {
  const [reducedData, setReducedData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReducedData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:5000/dimension_reduce", {
          params: { sub_ind: subspaceId, n_components: nComponents },
        });
        setReducedData(response.data.reduced_data || []);
      } catch (err) {
        setError("Failed to fetch dimensionality-reduced data.");
      }
    };

    fetchReducedData();
  }, [subspaceId, nComponents]);

  const generatePlotData = () => {
    if (!reducedData.length) return [];

    const x = reducedData.map((point) => point["compo_feature0"]);
    const y = reducedData.map((point) => point["compo_feature1"]);
    const z = nComponents === 3 ? reducedData.map((point) => point["compo_feature2"]) : null;
    const labels = reducedData.map((point) => point["target"]);

    const trace = {
      x,
      y,
      text: labels.map((label) => `Target: ${label}`), // Show target on hover
      type: "scatter",
      mode: "markers",
      marker: {
        color: labels,
        colorscale: "Viridis", // Color based on target values
        size: 8,
      },
    };

    if (z) {
      trace.z = z;
      trace.type = "scatter3d";
    }

    return [trace];
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {reducedData.length > 0 && (
        <Plot
          data={generatePlotData()}
          layout={{
            title: "Dimensionality Reduction Plot",
            xaxis: { title: "Dimension 1" },
            yaxis: { title: "Dimension 2" },
            ...(nComponents === 3 && { scene: { zaxis: { title: "Dimension 3" } } }),
            hovermode: "closest",
          }}
        />
      )}
    </div>
  );
};

export default DimensionalityReductionPlot;
