import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./home.css";

function HomePage() {
    const navigate = useNavigate();
    const [subspaces, setSubspaces] = useState([]);

    // Fetch subspaces from localStorage when the component mounts
    const loadSubspaces = () => {
        const savedSubspaces = JSON.parse(localStorage.getItem("subspaces")) || [];
        setSubspaces(savedSubspaces);
    };

    useEffect(() => {
        loadSubspaces();
    }, []);

    const handleCreateSubspace = () => {
        const newSubspace = {
            id: Date.now(),
            name: "New Subspace",
            attributes: [],
        };
        const updatedSubspaces = [...subspaces, newSubspace];
        setSubspaces(updatedSubspaces);
        localStorage.setItem("subspaces", JSON.stringify(updatedSubspaces));
        navigate(`/add-subspace?id=${newSubspace.id}`);
    };

    const handleEditSubspace = (id) => {
        navigate(`/add-subspace?id=${id}`);
    };

    const handleExploreSubspace = (id) => {
        navigate(`/explore?id=${id}`);
    };

    // Function to clear all subspaces
    const handleClearLocalStorage = () => {
        if (window.confirm("Are you sure you want to clear all subspaces? This action cannot be undone.")) {
            localStorage.removeItem("subspaces"); // Clear only subspaces, not everything
            setSubspaces([]); // Reset the state
            alert("All subspaces have been cleared.");
        }
    };

    return (
        <div className="home-page">
            <h1>Welcome to HeartViz</h1>
            <button onClick={handleCreateSubspace} className="create-btn">
                Create New Subspace
            </button>
            <button onClick={handleClearLocalStorage} className="clear-btn">
                Clear All Subspaces
            </button>
            {subspaces.length > 0 ? (
                <div className="subspaces-container">
                    {subspaces.map((subspace) => (
                        <div key={subspace.id} className="subspace-card">
                            <div className="subspace-name">{subspace.name}</div>
                            <div className="subspace-buttons">
                                <button
                                    onClick={() => handleEditSubspace(subspace.id)}
                                    className="edit-btn"
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleExploreSubspace(subspace.id)}
                                    className="explore-btn"
                                >
                                    Explore
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <p>No subspaces available. Create a new one to get started.</p>
            )}
        </div>
    );
}

export default HomePage;
