import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "./home.css"

function HomePage() {
    const navigate = useNavigate();
    const [showPopup, setShowPopup] = useState(false);
    const [subspaceName, setSubspaceName] = useState('');

    const handleOpenPopup = () => {
        setShowPopup(true);
    };

    const handleClosePopup = () => {
        setShowPopup(false);
    };

    const handleConfirm = () => {
        if (subspaceName.trim()) {
            // Save subspace name locally
            localStorage.setItem('subspaceName', subspaceName);
            navigate('/add-subspace');
        } else {
            alert('Please enter a valid subspace name');
        }
    };

    return (
        <div className="home-page">
            <h1>Welcome to HeartViz</h1>
            <button onClick={handleOpenPopup}>Add Subspace</button>

            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup">
                        <h2>Name Your Subspace</h2>
                        <input
                            type="text"
                            placeholder="Enter subspace name"
                            value={subspaceName}
                            onChange={(e) => setSubspaceName(e.target.value)}
                        />
                        <div className="popup-actions">
                            <button onClick={handleConfirm}>Confirm</button>
                            <button onClick={handleClosePopup}>Cancel</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default HomePage;
