import React, { useState, useEffect } from "react";
import RadarComparisonChart from "../components/radarChart/RadarComparisonRatio"; // Radar chart component
import "./multispace.css";

function MultiSpaceExplore() {
  const [allSubspaces, setAllSubspaces] = useState([]);
  const [selectedSubspace1, setSelectedSubspace1] = useState("");
  const [selectedSubspace2, setSelectedSubspace2] = useState("");

  useEffect(() => {
    // Load subspaces from localStorage
    const savedSubspaces = JSON.parse(localStorage.getItem("subspaces")) || [];
    setAllSubspaces(savedSubspaces);

    // Automatically set the first two subspaces for comparison
    if (savedSubspaces.length >= 2) {
      setSelectedSubspace1(savedSubspaces[0]?.id);
      setSelectedSubspace2(savedSubspaces[1]?.id);
    }
  }, []);

  const handleSubspaceChange = (subspaceId, subspaceNumber) => {
    if (subspaceNumber === 1) {
      setSelectedSubspace1(subspaceId);
    } else {
      setSelectedSubspace2(subspaceId);
    }
  };

  const renderTooltip = (subspaceId) => {
    const subspace = allSubspaces.find((s) => s.id === subspaceId);
    if (!subspace) return null;

    return (
      <div className="tooltip-content">
        {subspace.attributes && subspace.attributes.length > 0
          ? `Attributes: ${subspace.attributes.join(", ")}`
          : "No attributes selected for this subspace."}
      </div>
    );
  };

  return (
    <div className="explore-page">
      {/* Header Section */}
      <div className="header">
        <h1>Multi-Subspace Exploration</h1>
        <div className="subspace-selection">
          {/* Subspace 1 */}
          <div className="subspace-dropdown-container">
            <label>Subspace 1:</label>
            <div className="tooltip-container">
              <select
                className="subspace-dropdown"
                value={selectedSubspace1}
                onChange={(e) => handleSubspaceChange(e.target.value, 1)}
              >
                {allSubspaces.map((subspace) => (
                  <option key={subspace.id} value={subspace.id}>
                    {subspace.name}
                  </option>
                ))}
              </select>
              <button className="tooltip-button">?</button>
              {selectedSubspace1 && renderTooltip(selectedSubspace1)}
            </div>
          </div>

          {/* Subspace 2 */}
          <div className="subspace-dropdown-container">
            <label>Subspace 2:</label>
            <div className="tooltip-container">
              <select
                className="subspace-dropdown"
                value={selectedSubspace2}
                onChange={(e) => handleSubspaceChange(e.target.value, 2)}
              >
                {allSubspaces.map((subspace) => (
                  <option key={subspace.id} value={subspace.id}>
                    {subspace.name}
                  </option>
                ))}
              </select>
              <button className="tooltip-button">?</button>
              {selectedSubspace2 && renderTooltip(selectedSubspace2)}
            </div>
          </div>
        </div>
      </div>

      {/* Plots Section */}
      <div className="plots-container">
        {selectedSubspace1 && selectedSubspace2 && (
          <div className="plot-section">
            <h2>
              Subspace {selectedSubspace1} vs Subspace {selectedSubspace2} (AVG)
            </h2>
            <RadarComparisonChart
              subInd1={selectedSubspace1}
              subInd2={selectedSubspace2}
              metric="avg"
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default MultiSpaceExplore;
