import React from "react";
import "./infoPanel.css";

function InfoPanel({ isOpen, onClose }) {
  return (
    <>
      {isOpen && (
        <div className="info-panel-overlay" onClick={onClose}>
          <div
            className="info-panel"
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside
          >
            <h2>Information</h2>
            <p>
              Welcome to the tool! Here you can include detailed information
              about how to use the application, its features, and tips for
              users.
            </p>
            <button onClick={onClose} className="close-btn">
              Close
            </button>
          </div>
        </div>
      )}
    </>
  );
}

export default InfoPanel;
