import React, { useState, useEffect } from "react";
import Plot from "react-plotly.js";
import axios from "axios";

const DimensionalityReductionPlot = ({ subspaceId, nComponents = 2 }) => {
  const [reducedData, setReducedData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReducedData = async () => {
      try {
        const response = await axios.get(
          "http://127.0.0.1:5000/dimension_reduce",
          { params: { sub_ind: subspaceId, n_components: nComponents } }
        );

        console.log("Dimensionality Reduction Data:", response.data);

        if (response.data && typeof response.data === "object") {
          // Transform the data into an array of objects
          const transformedData = Object.keys(response.data).reduce(
            (acc, key) => {
              const dataLength = response.data[key].length;
              for (let i = 0; i < dataLength; i++) {
                if (!acc[i]) acc[i] = {};
                acc[i][key] = response.data[key][i];
              }
              return acc;
            },
            []
          );

          setReducedData(transformedData);
        } else {
          throw new Error("Invalid data format from API.");
        }
      } catch (err) {
        console.error("Error fetching dimensionality reduction data:", err);
        setError("Failed to fetch dimensionality reduction data.");
      }
    };

    if (subspaceId) {
      fetchReducedData();
    }
  }, [subspaceId, nComponents]);

  if (error) {
    return <p style={{ color: "red", textAlign: "center" }}>{error}</p>;
  }

  if (reducedData.length === 0) {
    return <p style={{ textAlign: "center" }}>Loading dimensionality reduction...</p>;
  }

  const plotData = reducedData.map((point) => ({
    x: [point.compo_feature0],
    y: [point.compo_feature1],
    type: "scatter",
    mode: "markers",
    marker: { size: 10 },
    name: `Class ${point.target}`,
  }));

  return (
    <div>
      <Plot
        data={plotData}
        layout={{
          title: "Dimensionality Reduction Plot",
          xaxis: { title: "Component 0" },
          yaxis: { title: "Component 1" },
          margin: { t: 50, b: 50 },
        }}
      />
    </div>
  );
};

export default DimensionalityReductionPlot;
