import React, { useState, useEffect } from "react";
import RadarComparisonChart from "../components/radarChart/RadarComparisonRatio";
import "./explore.css";

function MultiSpaceExplore() {
    const [allSubspaces, setAllSubspaces] = useState([]);
    const [selectedComparisons, setSelectedComparisons] = useState([]);

    useEffect(() => {
        const savedSubspaces = JSON.parse(localStorage.getItem("subspaces")) || [];
        setAllSubspaces(savedSubspaces);

        if (savedSubspaces.length >= 2) {
            setSelectedComparisons([
                {
                    subspace1: savedSubspaces[0].id,
                    subspace2: savedSubspaces[1].id,
                    metric: "avg",
                },
            ]);
        }
    }, []);

    const renderSubspaceNameWithTooltip = (subspaceId) => {
        const subspace = allSubspaces.find((s) => s.id === parseInt(subspaceId));
        if (!subspace) return "Unknown Subspace";

        return (
            <span className="subspace-name">
                {subspace.name}
                <div className="tooltip-container">
                    <button className="tooltip-button">?</button>
                    <div className="tooltip-content">
                        {subspace.attributes && subspace.attributes.length > 0 ? (
                            `Attributes: ${subspace.attributes.join(", ")}`
                        ) : (
                            "No attributes selected for this subspace."
                        )}
                    </div>
                </div>
            </span>
        );
    };

    return (
      <div className="explore-page">
          <div className="header">
              <h1 className="subspace-name">
                  Multi-Subspace Exploration:{" "}
                  {selectedComparisons.length > 0 &&
                      renderSubspaceNameWithTooltip(selectedComparisons[0].subspace1)}{" "}
                  -{" "}
                  {selectedComparisons.length > 0 &&
                      renderSubspaceNameWithTooltip(selectedComparisons[0].subspace2)}
              </h1>
          </div>

          <div className="plots-container">
              {selectedComparisons.length > 0 ? (
                  selectedComparisons.map((comparison, index) => (
                      <div key={index} className="plot-section">
                          <RadarComparisonChart
                              subInd1={comparison.subspace1}
                              subInd2={comparison.subspace2}
                              metric={comparison.metric}
                          />
                      </div>
                  ))
              ) : (
                  <p>No comparisons available.</p>
              )}
          </div>
      </div>
  );  
}
export default MultiSpaceExplore;
