import React, { useState } from "react";

function Filter({ name, min, max, onFilterChange }) {
  const [filterValues, setFilterValues] = useState({ min, max });

  const handleMinChange = (e) => {
    let newMin = Number(e.target.value);
    if (newMin > filterValues.max || newMin < min) {
      newMin = min;
    }
    setFilterValues({ ...filterValues, min: newMin });
    onFilterChange(name, { ...filterValues, min: newMin });
  };

  const handleMaxChange = (e) => {
    let newMax = Number(e.target.value);
    if (newMax < filterValues.min || newMax > max) {
      newMax = max;
    }
    setFilterValues({ ...filterValues, max: newMax });
    onFilterChange(name, { ...filterValues, max: newMax });
  };

  return (
    <div className="filter">
      <h3>{name}</h3>
      <div>
        <label>Min:</label>
        <input
          type="number"
          value={filterValues.min}
          onBlur={handleMinChange} 
          onChange={(e) => setFilterValues({ ...filterValues, min: Number(e.target.value) })}
        />
      </div>
      <div>
        <label>Max:</label>
        <input
          type="number"
          value={filterValues.max}
          onBlur={handleMaxChange}
          onChange={(e) => setFilterValues({ ...filterValues, max: Number(e.target.value) })}
        />
      </div>
    </div>
  );
}

export default Filter;
