import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import mockData from '../mockData'; // Mock data with actual values
import CorrelationHeatmap from '../components/CorrelationHeatmap'; // Heatmap component
import { calculateCorrelationMatrix } from '../utils/correlation'; // Correlation logic

function Explore() {
    const [searchParams] = useSearchParams();
    const subspaceId = searchParams.get('id');
    const [subspace, setSubspace] = useState(null);
    const [correlationMatrix, setCorrelationMatrix] = useState([]);
    const [numericAttributes, setNumericAttributes] = useState([]);

    useEffect(() => {
        const savedSubspaces = JSON.parse(localStorage.getItem('subspaces')) || [];
        const currentSubspace = savedSubspaces.find((s) => s.id === Number(subspaceId));
        setSubspace(currentSubspace);

        if (currentSubspace) {
            // Filter for numeric attributes
            const numericAttrs = currentSubspace.attributes.filter((attr) =>
                ["Age", "Cholesterol", "Blood Pressure", "Heart Rate", "BMI", "Maximum Heart Rate Achieved"]
                    .includes(attr.name)
            );

            setNumericAttributes(numericAttrs.map((attr) => attr.name));

            // Calculate correlation matrix
            const filteredData = mockData.map((row) =>
                Object.fromEntries(
                    numericAttrs.map((attr) => [attr.name, row[attr.name]])
                )
            );

            const matrix = calculateCorrelationMatrix(filteredData, numericAttrs.map((attr) => attr.name));
            setCorrelationMatrix(matrix);
        }
    }, [subspaceId]);

    return (
        <div className="explore-page">
            {subspace ? (
                <>
                    <h1>Explore Subspace: {subspace.name}</h1>
                    <p>Attributes: {subspace.attributes.map((attr) => attr.name).join(', ') || 'None selected.'}</p>
                    {correlationMatrix.length > 0 && numericAttributes.length > 0 ? (
                        <CorrelationHeatmap matrix={correlationMatrix} attributes={numericAttributes} />
                    ) : (
                        <p>No numeric attributes available for correlation analysis.</p>
                    )}
                </>
            ) : (
                <p>Subspace not found!</p>
            )}
        </div>
    );
}

export default Explore;
