import React, { useState, useEffect } from "react";
import { useSearchParams, useNavigate } from "react-router-dom";
import axios from "axios"; // For API calls
import "./subspaceCreator.css";

function AddSubspacePage() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const subspaceId = searchParams.get("id");

  const [subspace, setSubspace] = useState({
    name: "",
    attributes: [], // Dynamically fetched attributes from the backend
    selected: [], // User-selected attributes with ranges
  });

  const [currentSelectedAttribute, setCurrentSelectedAttribute] =
    useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [minRangeValue, setMinRangeValue] = useState("");
  const [maxRangeValue, setMaxRangeValue] = useState("");

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch attributes and ranges from the backend when the page loads
  useEffect(() => {
    const fetchAttributes = async () => {
      try {
        setLoading(true); // Set loading state
        const response = await axios.get("http://127.0.0.1:5000/feature_ranges"); // API call to fetch ranges

        // Transform the API response into the required format
        const attributes = Object.entries(response.data.feature_ranges).map(
          ([name, range]) => ({
            id: name, // Use the attribute name as a unique ID
            name, // Attribute name
            range, // [min, max] range from the backend
          })
        );

        // Update subspace state with fetched attributes
        setSubspace((prev) => ({
          ...prev,
          attributes,
        }));

        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch attributes:", err);
        setError("Failed to fetch attributes. Please try again later.");
        setLoading(false);
      }
    };

    fetchAttributes();
  }, []);

  // Confirm subspace or delete logic
  useEffect(() => {
    const handleConfirm = () => navigate("/");
    const handleDelete = () => {
      const savedSubspaces =
        JSON.parse(localStorage.getItem("subspaces")) || [];
      const updatedSubspaces = savedSubspaces.filter(
        (s) => s.id !== Number(subspaceId)
      );
      localStorage.setItem("subspaces", JSON.stringify(updatedSubspaces));
      navigate("/");
    };

    window.addEventListener("confirmSubspace", handleConfirm);
    window.addEventListener("deleteSubspace", handleDelete);

    return () => {
      window.removeEventListener("confirmSubspace", handleConfirm);
      window.removeEventListener("deleteSubspace", handleDelete);
    };
  }, [subspaceId, navigate]);

  // Handle name change
  const handleNameChange = (e) => {
    const updatedName = e.target.value;
    setSubspace({ ...subspace, name: updatedName });
    updateLocalStorage({ ...subspace, name: updatedName });
  };

  // Handle selecting an attribute
  const handleSelect = (attr) => {
    setCurrentSelectedAttribute(attr);
    const range = getRangeById(attr.id);
    if (range) {
      setMinRangeValue(range[0]);
      setMaxRangeValue(range[1]);
    } else {
      setMinRangeValue("");
      setMaxRangeValue("");
    }
  };

  // Save to local storage
  const updateLocalStorage = (updatedSubspace) => {
    const savedSubspaces = JSON.parse(localStorage.getItem("subspaces")) || [];
    const updatedSubspaces = savedSubspaces.map((s) =>
      s.id === updatedSubspace.id ? updatedSubspace : s
    );
    localStorage.setItem("subspaces", JSON.stringify(updatedSubspaces));
  };

  // Filter attributes based on the search term
  const filteredAttributes = subspace.attributes.filter((attr) =>
    attr.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Utility functions
  const getRangeById = (id) => {
    return subspace.selected.filter((i) => i.id === id)?.[0]?.range;
  };

  const isSelected = (id) => {
    return subspace.selected.filter((i) => i.id === id).length !== 0;
  };

  const maybeRemovedSelected = (id, selected) => {
    return selected.filter((element) => id !== element.id);
  };

  // Render loading or error state
  if (loading) return <div>Loading attributes...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="add-subspace-page">
      <div className="left-column">
        <div className="attributes-list">
          <h2>Available Attributes</h2>
          <input
            type="text"
            placeholder="Filter attributes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <ul>
            {filteredAttributes.map((attr) => (
              <li key={attr.id} onClick={() => handleSelect(attr)}>
                {attr.name}
              </li>
            ))}
          </ul>
        </div>
        <div className="selected-attributes">
          <input
            type="text"
            value={subspace.name}
            onChange={handleNameChange}
            placeholder="Subspace Name"
            className="subspace-name-input"
          />
          <ul>
            {subspace.selected.map((selectedAttribute) => (
              <li
                key={selectedAttribute.id}
                onClick={() =>
                  handleSelect({
                    id: selectedAttribute.id,
                    name: selectedAttribute.name,
                  })
                }
              >
                {selectedAttribute.name} : {selectedAttribute.range[0]} -{" "}
                {selectedAttribute.range[1]}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="filter-panel">
        {currentSelectedAttribute ? (
          <>
            <div>{currentSelectedAttribute.name}</div>
            <div>
              min:{" "}
              <input
                type="number"
                value={minRangeValue}
                onChange={(e) => setMinRangeValue(e.target.value)}
              />
            </div>
            <div>
              max:{" "}
              <input
                type="number"
                value={maxRangeValue}
                onChange={(e) => setMaxRangeValue(e.target.value)}
              />
            </div>

            <button
              className="confirm-button"
              onClick={() => {
                if (minRangeValue === "" || maxRangeValue === "") {
                  alert("Please input a value");
                  return;
                }
                if (Number(minRangeValue) >= Number(maxRangeValue)) {
                  alert("Min value cannot be greater than or equal to max value");
                  return;
                }
                const newSelected = maybeRemovedSelected(
                  currentSelectedAttribute.id,
                  subspace.selected
                );
                newSelected.push({
                  id: currentSelectedAttribute.id,
                  name: currentSelectedAttribute.name,
                  range: [Number(minRangeValue), Number(maxRangeValue)],
                });
                setSubspace({ ...subspace, selected: newSelected });
              }}
            >
              Confirm
            </button>
            {isSelected(currentSelectedAttribute.id) && (
              <button
                onClick={() =>
                  setSubspace({
                    ...subspace,
                    selected: maybeRemovedSelected(
                      currentSelectedAttribute.id,
                      subspace.selected
                    ),
                  })
                }
                className="delete-button"
              >
                Delete
              </button>
            )}
          </>
        ) : (
          <>
            <h2>Filter Options</h2>
            <p>Select an attribute to configure its range.</p>
          </>
        )}
      </div>
    </div>
  );
}

export default AddSubspacePage;
