import React, { useEffect, useState } from "react";
import HeatMap from "react-heatmap-grid";
import mockCorrelationMatrix from "../../mockDataM.json";

const CorrelationMatrixHeatmap = () => {
  const [matrixData, setMatrixData] = useState(null);

  useEffect(() => {
    // Simulate API call by loading mock data
    setMatrixData(mockCorrelationMatrix.correlation_matrix);
  }, []);

  if (!matrixData) {
    return <p>Loading...</p>;
  }

  // Extract features and correlation values
  const features = Object.keys(matrixData);
  const data = features.map((rowKey) => features.map((colKey) => matrixData[rowKey][colKey]));

  return (
    <div>
      <h1>Correlation Matrix Heatmap</h1>
      <HeatMap
        xLabels={features}
        yLabels={features}
        data={data}
        cellStyle={(background, value) => ({
          background: `rgba(0, 0, 255, ${Math.abs(value)})`,
          color: value > 0.5 || value < -0.5 ? "white" : "black",
        })}
        cellRender={(value) => value && value.toFixed(2)}
      />
    </div>
  );
};

export default CorrelationMatrixHeatmap;
