// export default ParallelCoordinatesPlot;

  const ParallelCoordinatesPlot = ({ data, dimensions }) => {
    const containerRef = useRef();

    useEffect(() => {
        if (!data || !dimensions) return;

        d3.select(containerRef.current).selectAll("*").remove();

        const margin = { top: 30, right: 40, bottom: 10, left: 10 };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;

        const svg = d3
            .select(containerRef.current)
            .append("svg")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform", `translate(${margin.left},${margin.top})`);

        const yScales = {};
        dimensions.forEach((dimension) => {
            yScales[dimension] = d3
                .scaleLinear()
                .domain(d3.extent(data, (d) => d[dimension]))
                .range([height, 0]);
        });

        const xScale = d3
            .scalePoint()
            .domain(dimensions)
            .range([0, width]);

        svg.selectAll(".dimension")
            .data(dimensions)
            .enter()
            .append("g")
            .attr("class", "dimension")
            .attr("transform", (d) => `translate(${xScale(d)})`)
            .each(function (dimension) {
                d3.select(this)
                    .call(d3.axisLeft(yScales[dimension]))
                    .append("text")
                    .attr("text-anchor", "middle")
                    .attr("y", -9)
                    .text(dimension);
            });

        svg.append("g")
            .selectAll(".path")
            .data(data)
            .enter()
            .append("path")
            .attr("d", (d) =>
                d3.line()(
                    dimensions.map((dimension) => [
                        xScale(dimension),
                        yScales[dimension](d[dimension]),
                    ])
                )
            )
            .style("fill", "none")
            .style("stroke", "#136F63")
            .style("stroke-width", 1.5)
            .style("opacity", 0.7);
    }, [data, dimensions]);

    return <div ref={containerRef}></div>;
};


export default ParallelCoordinatesPlot;
