import React, { useState, useEffect } from 'react';
import mockData from '../mockData';
import './subspaceCreator.css';

function AddSubspacePage() {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedAttributes, setSelectedAttributes] = useState([]);
    const [subspaceName, setSubspaceName] = useState('');

    useEffect(() => {
        // Retrieve the subspace from localStorage
        const savedSubspace = JSON.parse(localStorage.getItem('subspace'));
        if (savedSubspace) {
            setSubspaceName(savedSubspace.name);
            setSelectedAttributes(savedSubspace.attributes);
        }
    }, []);

    const saveSubspaceToLocalStorage = (name, attributes) => {
        const subspace = { name, attributes };
        localStorage.setItem('subspace', JSON.stringify(subspace));
    };

    const handleSelect = (attr) => {
        if (!selectedAttributes.includes(attr)) {
            const updatedAttributes = [...selectedAttributes, attr];
            setSelectedAttributes(updatedAttributes);
            saveSubspaceToLocalStorage(subspaceName, updatedAttributes);
        }
    };

    const handleDeselect = (attr) => {
        const updatedAttributes = selectedAttributes.filter(item => item !== attr);
        setSelectedAttributes(updatedAttributes);
        saveSubspaceToLocalStorage(subspaceName, updatedAttributes);
    };

    const handleNameChange = (e) => {
        const name = e.target.value;
        setSubspaceName(name);
        saveSubspaceToLocalStorage(name, selectedAttributes);
    };

    return (
        <div className="add-subspace-page">
            <div className="left-column">
                <div className="attributes-list">
                    <h2>Available Attributes</h2>
                    <input
                        type="text"
                        placeholder="Filter attributes..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <ul>
                        {mockData
                            .filter(attr =>
                                attr.name.toLowerCase().includes(searchTerm.toLowerCase())
                            )
                            .map(attr => (
                                <li key={attr.id} onClick={() => handleSelect(attr)}>
                                    {attr.name}
                                </li>
                            ))}
                    </ul>
                </div>
                <div className="selected-attributes">
                    <input
                        type="text"
                        placeholder="Subspace Name"
                        value={subspaceName}
                        onChange={handleNameChange}
                        className="subspace-name-input"
                    />
                    <ul>
                        {selectedAttributes.map(attr => (
                            <li key={attr.id} onClick={() => handleDeselect(attr)}>
                                {attr.name}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="filter-panel">
                <h2>Filter Options</h2>
                <p>Add your filter components and options here.</p>
            </div>
        </div>
    );
}

export default AddSubspacePage;
