import React, { useState, useEffect } from "react";
import RadarComparisonChart from "../components/radarChart/RadarComparisonRatio"; // Radar chart component
import "./explore.css";

function MultiSpaceExplore() {
  const [allSubspaces, setAllSubspaces] = useState([]);
  const [selectedComparisons, setSelectedComparisons] = useState([]);
  const [metric, setMetric] = useState("avg");

  useEffect(() => {
    // Load subspaces from localStorage
    const savedSubspaces = JSON.parse(localStorage.getItem("subspaces")) || [];
    setAllSubspaces(savedSubspaces);

    // Automatically set the first two subspaces for comparison
    if (savedSubspaces.length >= 2) {
      setSelectedComparisons([
        {
          subspace1: savedSubspaces[0].id,
          subspace2: savedSubspaces[1].id,
          metric: "avg", // Default metric
        },
      ]);
    }
  }, []);

  const handleMetricChange = (event) => {
    const newMetric = event.target.value;
    setMetric(newMetric);

    // Update the metric for all selected comparisons
    setSelectedComparisons((prevComparisons) =>
      prevComparisons.map((comparison) => ({
        ...comparison,
        metric: newMetric,
      }))
    );
  };

  return (
    <div className="explore-page">
      {/* Header Section */}
      <div className="header">
        <h1>Multi-Subspace Exploration</h1>
        <div className="subspace-name">
          <div>
            <label>Metric:</label>
            <select
              className="subspace-dropdown"
              value={metric}
              onChange={handleMetricChange}
            >
              <option value="max">Max</option>
              <option value="avg">Average</option>
            </select>
          </div>
        </div>
      </div>

      {/* Plots Section */}
      <div className="plots-container">
        {selectedComparisons.map((comparison, index) => (
          <div key={index} className="plot-section">
            <h2>
              Subspace {comparison.subspace1} vs Subspace {comparison.subspace2}{" "}
              ({comparison.metric.toUpperCase()})
            </h2>
            <RadarComparisonChart
              subInd1={comparison.subspace1}
              subInd2={comparison.subspace2}
              metric={comparison.metric}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default MultiSpaceExplore;
