import React from 'react';
import Plot from 'react-plotly.js';

const CorrelationMatrix = ({ data }) => {
    const attributes = Object.keys(data[0]);

    // Generate a mock correlation matrix (replace with real correlations if available)
    const mockCorrelationMatrix = attributes.map(() =>
        attributes.map(() => (Math.random() * 2 - 1).toFixed(2)) // Random values between -1 and 1
    );

    return (
        <Plot
            data={[
                {
                    z: mockCorrelationMatrix,
                    x: attributes,
                    y: attributes,
                    type: 'heatmap',
                    colorscale: 'RdBu', // Use a diverging color scale
                    reversescale: true,
                    showscale: true,
                },
            ]}
            layout={{
                title: 'Correlation Matrix',
                annotations: mockCorrelationMatrix.flatMap((row, i) =>
                    row.map((value, j) => ({
                        x: attributes[j],
                        y: attributes[i],
                        text: value,
                        font: {
                            size: 10,
                            color: 'black',
                        },
                        showarrow: false,
                    }))
                ),
                xaxis: {
                    title: 'Attributes',
                    automargin: true,
                },
                yaxis: {
                    title: 'Attributes',
                    automargin: true,
                },
                width: 600,
                height: 600,
            }}
        />
    );
};

export default CorrelationMatrix;
