import React, { useState } from "react";
import Tree from "react-d3-tree";

const ClusteringTreeComponent = ({ treeData }) => {
  const [hoveredNode, setHoveredNode] = useState(null);

  const handleNodeHover = (node) => {
    setHoveredNode(node);
  };

  const handleNodeOut = () => {
    setHoveredNode(null);
  };

  return (
    <div style={{ width: "100%", height: "600px" }}>
      <h2>Clustering Tree</h2>
      <Tree
        data={treeData}
        orientation="vertical"
        pathFunc="diagonal"
        translate={{ x: 400, y: 50 }}
        nodeSize={{ x: 150, y: 100 }}
        styles={{
          links: { stroke: "#136F63", strokeWidth: 2 },
          nodes: { node: { circle: { fill: "#136F63" } } },
        }}
        onNodeMouseOver={(node) => handleNodeHover(node.data)}
        onNodeMouseOut={handleNodeOut}
      />
      {hoveredNode && (
        <div style={{ position: "absolute", top: 10, left: 10, backgroundColor: "white", padding: "10px", borderRadius: "5px", boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)" }}>
          <p><strong>Node:</strong> {hoveredNode.name}</p>
          {hoveredNode.value && <p><strong>Value:</strong> {hoveredNode.value}</p>}
          {hoveredNode.children && (
            <p><strong>Children:</strong> {hoveredNode.children.length}</p>
          )}
        </div>
      )}
    </div>
  );
};

export default ClusteringTreeComponent;
