import React, { useState, useEffect, useRef } from "react";
import * as d3 from "d3";

const ParallelCoordinatesPlot = ({ subspaceId }) => {
    const containerRef = useRef();
    const [data, setData] = useState([]);
    const [dimensions, setDimensions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(
                    `http://127.0.0.1:5000/fetch_data_with_features?sub_ind=${subspaceId}`
                );
                const result = await response.json();
                if (result.data) {
                    setData(
                        result.data.map((row) => {
                            const mappedRow = {};
                            for (let key in row) {
                                mappedRow[key] = +row[key]; // Convert values to numbers
                            }
                            return mappedRow;
                        })
                    );
                    setDimensions(Object.keys(result.data[0])); // Use keys from the first row as dimensions
                } else {
                    throw new Error("Failed to fetch data.");
                }
            } catch (err) {
                setError(err.message || "An unknown error occurred.");
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [subspaceId]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h1>Parallel Coordinates Plot</h1>
            <div ref={containerRef}></div>
        </div>
    );
};

export default ParallelCoordinatesPlot;
