import React, { useState, useEffect } from "react";
import Plot from "react-plotly.js";
import axios from "axios";

const CorrelationMatrixHeatmap = ({ subspaceId }) => {
  const [matrixData, setMatrixData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCorrelationMatrix = async () => {
      try {
        const response = await axios.get(
          `http://127.0.0.1:5000/correlation_matrix?sub_ind=${subspaceId}`
        );
        if (response.data && response.data.correlation_matrix) {
          setMatrixData(response.data.correlation_matrix);
        } else {
          setError("Failed to load correlation matrix.");
        }
      } catch (err) {
        setError("Error fetching correlation matrix.");
      } finally {
        setLoading(false);
      }
    };

    fetchCorrelationMatrix();
  }, [subspaceId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  // Extract feature names and matrix values
  const features = Object.keys(matrixData);
  const zValues = features.map((rowKey) =>
    features.map((colKey) => matrixData[rowKey][colKey])
  );

  return (
    <div>
      <h1>Correlation Matrix Heatmap</h1>
      <Plot
        data={[
          {
            z: zValues,
            x: features,
            y: features,
            type: "heatmap",
            colorscale: "Portland",
            reversescale: true,
            zmid: 0,
          },
        ]}
        layout={{
          title: "Correlation Matrix",
          xaxis: {
            title: "Features",
            side: "bottom",
          },
          yaxis: {
            title: "Features",
            automargin: true,
          },
          width: 700,
          height: 700,
        }}
      />
    </div>
  );
};

export default CorrelationMatrixHeatmap;
