import React, { useState } from "react";
import "./App.css";
import NavigationBar from "./components/navigationBar/navBar";
import InfoPanel from "./components/infoPanel/infoPanel";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import HomePage from "./pages/home";
import AddSubspacePage2 from "./pages/subspaceC";
import Explore from "./pages/explore";

function App() {
  // State to manage the visibility of the Information Panel
  const [isInfoPanelOpen, setIsInfoPanelOpen] = useState(false);

  // Function to toggle the panel's visibility
  const toggleInfoPanel = () => {
    setIsInfoPanelOpen(!isInfoPanelOpen);
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar with toggleInfoPanel passed as a prop */}
        <NavigationBar onOpenInfoPanel={toggleInfoPanel} />

        {/* Routes */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/add-subspace" element={<AddSubspacePage2 />} />
          <Route path="/explore" element={<Explore />} />
        </Routes>

        <InfoPanel isOpen={isInfoPanelOpen} onClose={() => setIsInfoPanelOpen(false)} />
      </div>
    </Router>
  );
}

export default App;
